          <h3>Rating : {review.Rating}</h3>

        </div>
        <h2 className="reviewtext">{review.review}</h2>
        <ColoredLine color="red" />
      </div>



    );
  }
}

export default connect(
  () => ({}),
  { //openMovieModal 
  }
