import React from 'react';
import {Row, Col} from 'react-bootstrap';
import {connect} from 'react-redux';
import MovieCard from '../movie-card/movie-card.component';
import LoaderComponent from '../../common/loader.component';
import './movie-list.component.css'
import * as movieActions from '../movie-browser.actions';

const styles = {
  movieColumn: {
    marginBottom: 50
  }
}

class MovieList extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      sortState: 0
    };
    this.handleClick = this.handleClick.bind(this);
    movieActions.getTopMovies()
  }

  handleClick() {
    alert('Click happened');
  }

  render(){
    const {movies}=this.props;
    const {isLoading} = this.props;
    console.log("mosss",movies);
  const movieColumns = movies ? movies.map(movie => (

      <MovieCard movie={movie} styles={{backgroundColor:"black"}} />
  )) : null; 
    return (
      <div>
      <div className="filters">
          <h3 className="text">Filters  </h3>
          <button className="sortButton" onClick={this.handleClick}  >Sort</button>
      </div>
      <Row>
        {movieColumns}
        <LoaderComponent isLoading={isLoading} />
      </Row>
      </div>
    );
  }
}





export default MovieList;
