  constructor(props) {
    super(props);
    // Track if the mouse hovering over the movie card

  }
  componentWillReceiveProps(nextProps){
    // If we will receive a new movieId
    console.log("####################comom", nextProps);
    //alert("dasdsad",nextProps);
  }
  render() {
    console.log("reviewcard",this.props);

    const {review} = this.props;
    // The CardTitle.subtitle won't render if it's null

    console.log("qwer",review.review_id);
    return (


      <Card


      >
        <CardMedia
          style={styles.cardMedia}
          overlay={
            <CardTitle
              title={review.review} 
              subtitle={review.rating} 
            />
          }
        >

        </CardMedia>
      </Card>



    );
  }
}

export default connect(
  () => ({}),
  { //openMovieModal 
  }
)(ReviewCardComponent);
